var allLang = ['en', 'de', 'es', 'fr', 'ja', 'pt'];
var systemLang = document.querySelector('html');
function chageUrlLang() {
  var curentLang = checkBrowserLang() || 'en';
  var urlParams = new URLSearchParams(window.location.search);
  urlParams.set('lang', curentLang);
  var newUrl = "".concat(window.location.origin).concat(window.location.pathname, "?").concat(urlParams.toString());
  if (!window.location.search) {
    curentLang = checkBrowserLang() || 'en';
    window.history.replaceState(null, '', newUrl);
  } else {
    curentLang = window.location.search.slice(6);
  }
  if (!allLang.includes(curentLang)) {
    curentLang = 'en';
    window.history.replaceState(null, '', newUrl);
  }
  systemLang.setAttribute('lang', curentLang);
  for (var key in localeList) {
    var elem = document.querySelector("[data-lang=".concat(key, "]"));
    if (elem) {
      elem.innerHTML = localeList[key][curentLang];
    }
  }
}
chageUrlLang();
function checkBrowserLang() {
  var navLang = navigator.language.slice(0, 2).toLowerCase();
  var result = allLang.some(function (elem) {
    return elem === navLang;
  });
  if (result) {
    return navLang;
  }
}